function i(n){const t=new Date(n),e=t.getFullYear(),a=String(t.getMonth()+1).padStart(2,"0"),r=String(t.getDate()).padStart(2,"0"),o=String(t.getHours()).padStart(2,"0"),s=String(t.getMinutes()).padStart(2,"0");return`${e}/${a}/${r} ${o}:${s}`}function u(n){const t=new Date(n),e=String(t.getHours()).padStart(2,"0"),a=String(t.getMinutes()).padStart(2,"0");return`${e}:${a}`}function g(n,t=1){return`${(n*100).toFixed(t)}%`}export{u as a,g as b,i as f};
